mapper.select:
select cib.co_infoId as 'co_infoId', cib.co_email as 'co_email', cib.co_title as 'co_title', cib.content as 'content', (select count(co_infoId) from CoLikeOfInfoBoard where co_infoId = #{co_infoId}) as 'co_likeBoardCount', (select SUM(C.cnt) from( select count(ccoib.co_infoId) as cnt from CoCommentOfInfoBoard as ccoib where ccoib.co_infoId = #{co_infoId}
UNION ALL
select count(crcoib.co_coib) as cnt from CoReCommentOfInfoBoard as crcoib left join CoCommentOfInfoBoard as ccoib on crcoib.co_coib = ccoib.co_coib where ccoib.co_infoId = #{co_infoId} )C) as 'commentCount', if(cmoib.co_moib, 1, 0) as 'co_mark', cib.createdAt as 'createdAt', cib.updatedAt as 'updatedAt' from CoInfoBoard as cib left join (select co_infoId, co_moib from CoMarkOfInfoBoard where co_infoId = #{co_infoId} and co_email = #{co_email}) cmoib on cib.co_infoId = cmoib.co_infoId where cib.co_infoId = #{co_infoId};

select co_coib, co_infoId, co_email, content, createdAt
from CoCommentOfInfoBoard
where co_infoId = #{co_infoId}

select co_rcoib, co_coib, co_email, content, createdAt
from CoReCommentOfInfoBoard
where co_coib = #{co_coib}

mapper.update:
UPDATE CoInfoBoard
SET co_title = #{co_title},
    content = #{content}
WHERE co_infoId = #{co_infoId}
