mapper.select:
select cqb.co_qnaId as 'co_qnaId', cqb.co_email as 'co_email', cqb.co_title as 'co_title', cqb.content as 'content', (select count(co_qnaId) from CoLikeOfQnaBoard where co_qnaId = #{co_qnaId}) as 'co_likeBoardCount', (select SUM(C.cnt) from( select count(ccoqb.co_qnaId) as cnt from CoCommentOfQnaBoard as ccoqb where ccoqb.co_qnaId = #{co_qnaId}
UNION ALL
select count(crcoqb.co_coqb) as cnt from CoReCommentOfQnaBoard as crcoqb left join CoCommentOfQnaBoard as ccoqb on crcoqb.co_coqb = ccoqb.co_coqb where ccoqb.co_qnaId = #{co_qnaId} )C) as 'commentCount', if(cmoqb.co_moqb, 1, 0) as 'co_mark', cqb.createdAt as 'createdAt', cqb.updatedAt as 'updatedAt' from CoQnaBoard as cqb left join (select co_qnaId, co_moqb from CoMarkOfQnaBoard where co_qnaId = #{co_qnaId} and co_email = #{co_email}) cmoqb on cqb.co_qnaId = cmoqb.co_qnaId where cqb.co_qnaId = #{co_qnaId};

select co_coqb, co_qnaId, co_email, content, createdAt
from CoCommentOfQnaBoard
where co_qnaId = #{co_qnaId}

select co_rcoqb, co_coqb, co_email, content, createdAt
from CoReCommentOfQnaBoard
where co_coqb = #{co_coqb}
