mapper.select:
select cqb.co_qnaId as 'co_qnaId', cqb.co_email as 'co_email', cu.co_nickname as 'co_nickname', cqb.co_title as 'co_title', cqb.content as 'content', (select count(co_qnaId) from CoLikeOfQnaBoard where co_qnaId = #{co_qnaId}) as 'co_likeCount', (select SUM(C.cnt) from( select count(ccoqb.co_qnaId) as cnt from CoCommentOfQnaBoard as ccoqb where ccoqb.co_qnaId = #{co_qnaId}
UNION ALL
select count(crcoqb.co_coqb) as cnt from CoReCommentOfQnaBoard as crcoqb left join CoCommentOfQnaBoard as ccoqb on crcoqb.co_coqb = ccoqb.co_coqb where ccoqb.co_qnaId = #{co_qnaId} )C) as 'co_commentCount', if(cmoqb.co_moqb, 1, 0) as 'co_mark', if(cloqb.co_loqb, 1, 0) as 'co_like', cqb.createdAt as 'createdAt', cqb.updatedAt as 'updatedAt' from CoQnaBoard as cqb left join CoUser cu on cqb.co_email = cu.co_email left join (select co_qnaId, co_moqb from CoMarkOfQnaBoard where co_qnaId = #{co_qnaId} and co_email = #{co_viewer}) cmoqb on cqb.co_qnaId = cmoqb.co_qnaId left join (select co_qnaId, co_loqb from CoLikeOfQnaBoard where co_qnaId = #{co_qnaId} and co_email = #{co_viewer}) cloqb on cqb.co_qnaId = cloqb.co_qnaId where cqb.co_qnaId = #{co_qnaId};

mapper.update:
UPDATE CoQnaBoard
SET co_title = #{co_title},
    content = #{content}
WHERE co_qnaId = #{co_qnaId}

mapper.select:
select distinct cqb.co_qnaId as 'co_qnaId', cqb.co_email as 'co_email', cu.co_nickName as 'co_nickname', cu.profileImg as 'profileImg', cqb.co_title as 'co_title', substr(cqb.content, 1, 40) as 'content', cqb.co_mainImg as 'co_mainImg', (select count(*) from CoLikeOfQnaBoard cloqb where cloqb.co_qnaId = cqb.co_qnaId) as 'co_likeCount', (select (select count(*) from CoCommentOfQnaBoard ccoqb2 where ccoqb2.co_qnaId = cqb.co_qnaId) + (select count(*) from CoReCommentOfQnaBoard crcoqb where crcoqb.co_coqb = ccoqb.co_coqb)) as 'co_commentCount', (select count(*) from CoMarkOfQnaBoard cmoqb where cmoqb.co_qnaId = cqb.co_qnaId) as 'co_markCount', cqb.createdAt as 'createdAt', cqb.updatedAt as 'updatedAt' from CoQnaBoard cqb left join CoUser cu on cqb.co_email = cu.co_email left join CoCommentOfQnaBoard ccoqb on cqb.co_qnaId = ccoqb.co_qnaId where cqb.createdAt is not null
and cqb.co_email = #{coMyBoard}
order by
co_likeCount desc limit
createdAt desc limit
#{limit} offset #{offset}
